/**
 * File: PlayerDetail.js
 * Version: Pre-Production
 * Created by: K. Zimmerman
 * Project: kc_royals_backend
 * Description:
 * - Displays detailed player statistics with all fields.
 * - Fixes missing fields and improves error handling.
 */

import React, { useEffect, useState } from "react";
import { fetchPlayerDetails } from "../api/api";

const PlayerDetail = ({ playerId }) => {
  const [player, setPlayer] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (!playerId) return;

    fetchPlayerDetails(playerId)
      .then((data) => {
        if (!data) {
          setError("Player not found.");
          return;
        }
        setPlayer(data);
      })
      .catch((err) => {
        console.error("Error fetching player details:", err);
        setError("Error retrieving player details.");
      });
  }, [playerId]);

  if (error) {
    return <p className="text-danger">{error}</p>;
  }

  if (!player) {
    return <p>Loading...</p>;
  }

  return (
    <div className="container mt-4">
      <h2>
        {player.first_name} {player.last_name}
      </h2>
      <p>
        <strong>Team:</strong> {player.team}
      </p>
      <p>
        <strong>Position:</strong> {player.position}
      </p>
      <p>
        <strong>Birth Date:</strong> {player.birth_date}
      </p>
      <p>
        <strong>Height:</strong>{" "}
        {player.height_feet ? `${player.height_feet} ft` : "N/A"}
      </p>
      <p>
        <strong>Weight:</strong> {player.weight} lbs
      </p>
      <p>
        <strong>Throws:</strong> {player.throws}
      </p>
      <p>
        <strong>Bats:</strong> {player.bats}
      </p>
    </div>
  );
};

export default PlayerDetail;
