/**
 * File: App.js
 * Version: Fixed - v3.0
 * Created by: K. Zimmerman
 * Project: kc_royals_backend
 * Description:
 * - Fixes React Router to ensure Home.js updates correctly.
 * - Forces React to reload Home.js when changes are made.
 */

import React from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Navigate,
  useLocation,
} from "react-router-dom";
import Navbar from "./components/Navbar";
import { AuthProvider } from "./context/AuthContext";
import { PlayerProvider } from "./context/PlayerContext";
import Home from "./pages/Home";

function App() {
  return (
    <AuthProvider>
      <PlayerProvider>
        <Router forceRefresh={true}>
          <Navbar />
          <main className="container mt-3">
            <Routes>
              <Route
                path="/"
                element={<ForceUpdateWrapper component={<Home />} />}
              />
              <Route path="*" element={<Navigate to="/" />} />
            </Routes>
          </main>
        </Router>
      </PlayerProvider>
    </AuthProvider>
  );
}

//  Wrapper to force component updates
const ForceUpdateWrapper = ({ component }) => {
  const location = useLocation();
  return React.cloneElement(component, { key: location.pathname });
};

export default App;
